    private void addNumber(String v) {
        if (estado == 1 || estado == 3) {
            jVisor.setText("0");
            estado = 2;
            // APAGAR O TEXTO VERMELHO DO BOTAO!!!!
        }
        String atual = jVisor.getText();

        if ("0".equals(atual) && !v.equals(".")) {
            jVisor.setText(v);
        } else {
            jVisor.setText(atual + v);
        }
    }
