    float numero1;
    float numero2;
    float resultado;
    char operacao = ' ';
    int estado = 0;

    private void fOperacao(char o, java.awt.event.ActionEvent evt) {
        if(estado == 2){
            operacao = o;
            jButtonSolveActionPerformed(evt);
        }
        numero1 = Float.valueOf(jVisor.getText());
        operacao = o;
        //jVisor.setText("0");
        estado = 1;
        
    }


    private void jButtonAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonAddActionPerformed
        fOperacao('+', evt);
    }//GEN-LAST:event_jButtonAddActionPerformed

    private void jButtonMultiplicateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonMultiplicateActionPerformed
        fOperacao('*', evt);
    }//GEN-LAST:event_jButtonMultiplicateActionPerformed

    private void jButtonSolveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonSolveActionPerformed
        if (estado == 0) {
            numero1 = Float.valueOf(jVisor.getText());
        }
        if (estado != 3) {
            numero2 = Float.valueOf(jVisor.getText());
        }
        switch (operacao) {
            case '+':
                resultado = numero1 + numero2;
                break;
            case '-':
                resultado = numero1 - numero2;
                break;
            case '*':
                resultado = numero1 * numero2;
                break;
            case '/':
                resultado = numero1 / numero2;
                break;
            default:
                resultado = numero1;
        }
        numero1 = resultado;
        jVisor.setText(String.valueOf(resultado));
        estado = 3;
    }//GEN-LAST:event_jButtonSolveActionPerformed

    private void jButtonDivideActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonDivideActionPerformed
        fOperacao('/', evt);
    }//GEN-LAST:event_jButtonDivideActionPerformed

    private void jButtonSubtractActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonSubtractActionPerformed
        fOperacao('-', evt);
    }//GEN-LAST:event_jButtonSubtractActionPerformed

    private void jButtonClearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonClearActionPerformed
        jVisor.setText("0");
        numero1 = 0;
        numero2 = 0;
        resultado = 0;
        operacao = ' ';
        estado = 0;
    }//GEN-LAST:event_jButtonClearActionPerformed

